@Composable
fun BookList(
    modifier: Modifier = Modifier,
    books: List<Book>,
    onBookClick: (Book) -> Unit = {},
    onAddMore: () -> Unit = {}
) {

    LazyVerticalGrid(
        columns = GridCells.Adaptive(400.dp),
        modifier = modifier.fillMaxWidth()
    ) {
        items(books) { book ->
            Card(
                border = BorderStroke(1.dp, Color.Gray),
                modifier = Modifier
                    .fillMaxWidth()
                    .height(180.dp)
                    .padding(4.dp)
                    .clickable(onClick = { onBookClick(book) })
            ) {
                Row(
                    Modifier
                        .padding(8.dp)
                        .fillMaxSize(),
                    horizontalArrangement = Arrangement.spacedBy(8.dp)
                ) {
                    AsyncImage(
                        model = ImageRequest.Builder(LocalContext.current)
                            .data(book.cover)
                            .crossfade(true)
                            .build(),
                        contentDescription = null,
                        contentScale = ContentScale.Crop,
                        modifier = Modifier
                            .fillMaxHeight()
                            .weight(1f)
                            .clip(RoundedCornerShape(12)),
                        placeholder = debugPlaceholder(R.drawable.ic_launcher_background)
                    )
                    Column(
                        Modifier
                            .fillMaxSize()
                            .weight(2f),
                        verticalArrangement = Arrangement.spacedBy(4.dp)
                    ) {
                        Text(
                            text = book.title,
                            modifier = Modifier
                                .fillMaxWidth(),
                            textAlign = TextAlign.Center,
                            style = MaterialTheme.typography.titleMedium,
                            maxLines = 3,
                            overflow = TextOverflow.Ellipsis
                        )

                        Row(verticalAlignment = Alignment.CenterVertically) {
                            Icon(imageVector = Icons.Filled.Person, contentDescription = null)
                            Text(
                                text = book.authors.joinToString { it.fullName },
                                style = MaterialTheme.typography.bodyMedium,
                            )
                        }
                        Row(verticalAlignment = Alignment.CenterVertically) {
                            Icon(imageVector = Icons.Filled.Mic, contentDescription = null)
                            Text(
                                text = book.voiceovers.first().readers.first().fullName,
                                style = MaterialTheme.typography.bodyMedium
                            )
                        }
                    }
                }
            }
        }
        if (books.isNotEmpty()) {
            item(
                span = { GridItemSpan(maxLineSpan) }
            ) {
                Button(
                    onClick = onAddMore,
                    modifier = Modifier
                        .padding(5.dp)
                        .fillMaxWidth()
                ) {
                    Text(text = "MORE")
                }
            }
        }

    }
}
